mapper.select:
SELECT id, user_id, english_expression, korean_meaning, created_at, is_memorized
FROM USER_VOCABULARY
WHERE id = #{id} AND user_id = #{userId}

mapper.update:
UPDATE USER_VOCABULARY
SET is_memorized = #{isMemorized}
WHERE id = #{id} AND user_id = #{userId}

mapper.select:
SELECT COUNT(*) > 0
FROM USER_VOCABULARY
WHERE id = #{id}
  AND user_id = #{userId}
